import React, { Component } from 'react'

export default class Fetchme extends Component {
    constructor(props) {
        super(props);
        this.state = {
          data: null,
          statebus: []
        };
    }
    // componentDidMount() {
    //     fetch(`https://www.redbus.in/Home/SolarSearch?search=${this.state.busarea}`)
    //       .then(response => response.json())
    //       .then(parsedJSON => {
    //         let buses = parsedJSON.response.docs.map((redbus)=>{
    //           return(
    //           <div>{redbus.Name}</div>
    //           )
    //         })
    //           this.setState({statebus:[buses]})
    //       })

    //   }

      srconchange = (e) =>{
          this.setState({[e.target.name]:e.target.value})
          fetch(`https://www.redbus.in/Home/SolarSearch?search=${this.state.bus}`)
          .then(response => response.json())
          .then(parsedJSON => {
            let buses = parsedJSON.response.docs.map((redbus)=>{
              return(
              <div>{redbus.Name}</div>
              )
            })
              this.setState({statebus:[buses]})
          })

      }

    render() {

        return (
            <div>
              <input type="text" name ="busarea" onChange={this.srconchange.bind(this)}></input>
        {this.state.statebus}

        <p> {this.state.busarea} </p>

      </div>
        );
    }
}
